import React, { Component } from 'react';
import { Alert, Button, StyleSheet, TextInput, ScrollView, ActivityIndicator, View } from 'react-native';
import db from '../firebase'




class Listq extends Component {

constructor(){
  super();
  this.fiestoreRef = db.collection('food');
  this.state ={
    isLoading:true,
    userArr: []
  };
}

componentDidMount() {
  this.unsubscribe = this.fiestoreRef.onSnapshot(this.getcollection);
}

componentWillUnmount(){
  this.unsubscribe();
}

getcollection = (querySnapshot) => {
  const userArr =[];
  querySnapshot.forEach((res) =>{
    const {name,img,price} = res.data();
    userArr.push({
      key:res.id,
      res,
      name,
      img,
      price,
    });
  });
  this.setState({
    userArr,
    isLoading:false,
  })
}

render(){
  if(this.state.isLoading){
    this.return(
      <View style={styles.preloader}>
          <ActivityIndicator size="large" color="#9E9E9E"/>
        </View>
    )
  }

  return (
     <ScrollView style={styles.container}>
          {
            this.state.userArr.map((item, i) => {
              return (
                <ListItem
                  key={i}
                  chevron
                  bottomDivider
                  title={item.name}
                  subtitle={item.email}
                  onPress={() => {
                    this.props.navigation.navigate('UserDetailScreen', {
                      userkey: item.key
                    });
                  }}/>
              );
            })
          }
      </ScrollView>
  );
        }
}

const styles = StyleSheet.create({
  container: {
   flex: 1,
   paddingBottom: 22
  },
  preloader: {
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    position: 'absolute',
    alignItems: 'center',
    justifyContent: 'center'
  }
})

export default Listq;
