import React, { useState, useEffect } from 'react';
import { ActivityIndicator, Image,FlatList, View, Text,SafeAreaView,StatusBar,StyleSheet,ScrollView } from 'react-native';
import Ionicons from 'react-native-vector-icons/Ionicons';
import db from '../firebase';



function Users() {
  const [loading, setLoading] = useState(true); // Set loading to true on component mount
  const [users, setUsers] = useState([]); // Initial empty array of users

  useEffect(() => {
    const subscriber = db
    .collection('food')
    .onSnapshot(querySnapshot => {
      const users = [];

      querySnapshot.forEach(documentSnapshot => {
        users.push({
          ...documentSnapshot.data(),
          key: documentSnapshot.id,
        });
      });

      setUsers(users);
      setLoading(false);
    });

  return () => subscriber();
  }, []);

  if (loading) {
    return <ActivityIndicator />;
  }
  
  const Separator = () => (
    <View style={styless.separator} />
  );
  
  const Item = ({ title,kak,pr }) => (
    <View style={styles.item}>
      <View style={{flex:1}}>
      <Text style={styles.title}>{kak}</Text>
      <Image
       style={{width: '100%', height: 200,resizeMode : 'stretch' ,borderRadius:30}}
       source={{uri: title}} 
       />  
      <View style={{flex:1,alignItems:'flex-start'}}>
      <Ionicons
      name='ios-add-circle-outline'
      size={30}
      />
       <Text>{pr}</Text>
       </View>
       <View style={{flex:1,alignItems:'flex-end'}}>
       <Ionicons
      name='ios-add-circle-outline'
      size={30}
      />
      </View>
      </View>
    </View>
  );

  const renderItem = ({ item }) => (
    <Item title={item.img} kak={item.name} pr={item.price} />
  );


  return (
    <SafeAreaView style={styles.container}>
    <FlatList
    
      data={users}
      renderItem={renderItem}
      keyExtractor={item => item.id}
    />
    </SafeAreaView>
  );
}




export default Users;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    backgroundColor: '#d8d6c8',
    padding: 10,
    marginVertical: 8,
    marginHorizontal: 16,
    borderRadius:30
  },
  title: {
    fontSize: 32,
  },
});
