import React,{Component} from 'react'
import firebase from './firebase'

class ComponentToPrint extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      names: [],
      prices: []
    };
  };

  componentDidMount(){
    const food = firebase.database().ref('name');
    food.on('value',(snapshot) => {
      let names = snapshot.val();
      let newstate = [];
      for (let name in names){
        newstate.push({
          id: name,
          name: names[name].name,
          price: names[name].price
        });
      }

      this.setState({
        names: newstate
      });

      const fod = firebase.database().ref('price');
      fod.on('value',(snapshot) =>{
        let names = snapshot.val();
      let newstate = [];
      for (let name in names){
        newstate.push({
          id: name,
          name: names[name].name,
          price: names[name].price
        });
      }});
      
  
    })
  }

  render(){
    return(
      <>
      <h1>hiii</h1>
      {this.state.names.map((name) => {
return(
  <h3>{name.name}:</h3>
)
      })}
      </>
    )
  }
}

export default ComponentToPrint;
