import firebase from "firebase";
import { render } from "react-dom";
import * as React from 'react';
import { SafeAreaView,FlatList } from 'react-native';

const firebaseApp = firebase.initializeApp({
  apiKey: "AIzaSyAHPX4OE5uYV4dZgpgnMnXOBuIM-65OjM0",
  authDomain: "myfood-27388.firebaseapp.com",
  databaseURL: "https://myfood-27388-default-rtdb.firebaseio.com",
  projectId: "myfood-27388",
  storageBucket: "myfood-27388.appspot.com",
  messagingSenderId: "815017237760",
  appId: "1:815017237760:web:c887a3071112e89b0167e5",
  measurementId: "G-JTLHX0ZCM2"
});

function SampleFunction (item){
 
  Alert.alert(item);

}

const db = firebaseApp.firestore();
export default function see(){
  
  firebaseApp.firestore().collection("food")
.get()
.then(querySnapshot => {
  const data = querySnapshot.docs.map(doc => doc.data());
  console.log(data); // array of cities objects
  
  showArrayItem = (item) => {
 
    Alert.alert(item);
 
  }
    return (
 
      <View style={styles.MainContainer}>
 
        <ScrollView>
 
          {
            this.firebaseApp.firestore().collection("food").data().map((item, key) => (
 
              <TouchableOpacity key={key} onPress={this.showArrayItem.bind(this, item.name)}>
 
                <Text style={styles.TextStyle} > Name = {item.name} </Text>
 
                <Text style={styles.TextStyle} > Age = {item.price} </Text>
 
                <Text  style={styles.TextStyle} > Class = {item.price} </Text>
 
                <View style={{ width: '100%', height: 1, backgroundColor: '#000' }} />
 
              </TouchableOpacity>
 
            ))
          }
 
        </ScrollView>
 
      </View>
    );
  }

 

);
   

}
