// src/views/Details.js
import React, { useState, useEffect } from 'react';
import { View, Text,Image,Button } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import db from '../firebase'
import { useRoute } from '@react-navigation/native';
import { StyleSheet,  TouchableHighlight } from "react-native";
import Basket from './basket';

function DetailsScreen()  {
  const navigation = useNavigation();
  const route =useRoute();
  const {param1, param2,param3} = route.params;
 
  const { products } = db.collection('food')
  .collection('food');
  const [cartItems, setCartItems] = useState([]);
  const onAdd = (product) => {
    const exist = cartItems.find((x) => x.id === product.id);
    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x.id === product.id ? { ...exist, qty: exist.qty + 1 } : x
        )
      );
    } else {
      setCartItems([...cartItems, { ...product, qty: 1 }]);
    }
  };
  const onRemove = (product) => {
    const exist = cartItems.find((x) => x.id === product.id);
    if (exist.qty === 1) {
      setCartItems(cartItems.filter((x) => x.id !== product.id));
    } else {
      setCartItems(
        cartItems.map((x) =>
          x.id === product.id ? { ...exist, qty: exist.qty - 1 } : x
        )
      );
    }
  };
 
      return (
        <View style={{flex: 1,flexDirection: 'column',}}>
        <View style={{  alignItems:'flex-start'}}>
          <Image
       style={{width: '100%', alignSelf:'flex-start',height: 200,resizeMode : 'stretch' ,borderRadius:10,marginTop:20}}
       source={{uri: param3}} 
       />
       </View>
       <View>
      <TouchableHighlight>
        <View style={{flexDirection:'row',backgroundColor: '#d8d6c8',justifyContent: 'space-around',height:100}}>
          <Text>{param1}-{param2}</Text>
          <Button onClick={() => onAdd(product)}>Add To Cart</Button>
        </View>
      </TouchableHighlight>
      </View>
      <View style={styles.countContainer}>
        <Basket cartItems={cartItems}  onAdd={onAdd} onRemove={onRemove}>
          {param2}
        </Basket>
      </View>
    </View>
      );
    }


export default DetailsScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    paddingHorizontal: 10
  },
  button: {
    alignItems: "center",
    backgroundColor: "#DDDDDD",
    padding: 10
  },
  
});
