import React, { useState, useEffect } from 'react';
import { ActivityIndicator, navigate,Image,FlatList, Button,View, Text,SafeAreaView,StatusBar,StyleSheet,ScrollView,  } from 'react-native';

import db from '../firebase';
import { useNavigation } from '@react-navigation/native';
import Cart from './cart';
import Basket from './basket'

import Ionicons from 'react-native-vector-icons/Ionicons';


function Addto() {
  const [loading, setLoading] = useState(true); // Set loading to true on component mount
  const [users, setUsers] = useState([]); // Initial empty array of users
  const navigation = useNavigation();

  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {

     const subscriber = db
    .collection('food')
    .onSnapshot(querySnapshot => {
      const users = [];

      querySnapshot.forEach(documentSnapshot => {
        users.push({
          ...documentSnapshot.data(),
          key: documentSnapshot.id,
        });
      });

      setUsers(users);
      setLoading(false);
    });


    

  return () => subscriber();
  }, []); 



  if (loading) {
    return <ActivityIndicator />;
  }
  
  const { products } = users;
  const Separator = () => (
    <View style={styless.separator} />
  );
 

  const onAdd = (item) => {
    const exist = cartItems.find((x) => x.id === item.id);
    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x.id === item.id ? { ...exist, qty: exist.qty + 1 } : x
        )
      );
    } else {
      setCartItems([...cartItems, { ...item, qty: 1 }]);
    }
  };
  const onRemove = (item) => {
    const exist = cartItems.find((x) => x.id === item.id);
    if (exist.qty === 1) {
      setCartItems(cartItems.filter((x) => x.id !== item.id));
    } else {
      setCartItems(
        cartItems.map((x) =>
          x.id === item.id ? { ...exist, qty: exist.qty - 1 } : x
        )
      );
    }
  };
 



  const Item = ({ title,kak,pr }) => (
    <View style={styles.item}>
      <View style={{flex:1}}>
      <Text style={styles.title}>{kak}</Text>
      <Image
       style={{width: '100%', height: 200,resizeMode : 'stretch' ,borderRadius:30}}
       source={{uri: title}} 
       />  
       <View style={{flexDirection:'row',justifyContent: 'space-between'}}>
       <Text style={{fontSize: 29,marginLeft: 10}}>RS:{pr}</Text>
       <Ionicons
       style={{marginRight: 10}}
       
      name='ios-add-circle-outline'
      size={32}
      />
      </View>
      </View>
    </View>
  );

  const renderItem = ({ item }) => (
    <Item title={item.img} kak={item.name} pr={item.price} />
  );


  return (
    <Basket
          cartItems={cartItems}
          onAdd={onAdd}
          onRemove={onRemove}
        ></Basket>    
   
  );
}




export default Addto;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    backgroundColor: '#d8d6c8',
    padding: 10,
    marginVertical: 8,
    marginHorizontal: 16,
    borderRadius:30
  },
  title: {
    fontSize: 32,
  },
});
