const onRemove = (item) => {
  const [cartItems, setCartItems] = useState([]);
  const exist = cartItems.find((x) => x.id === item.id);
  if (exist.qty === 1) {
    setCartItems(cartItems.filter((x) => x.id !== item.id));
  } else {
    setCartItems(
      cartItems.map((x) =>
        x.id === item.id ? { ...exist, qty: exist.qty - 1 } : x
      )
    );
  }
};
export default onRemove;
