import React, { useState, useEffect } from 'react';
import { ActivityIndicator, Image,FlatList, View, Text,SafeAreaView,StatusBar,StyleSheet,ScrollView } from 'react-native';
import firestore from '@react-native-firebase/firestore';
import db from '../firebase';



function Users() {
  const [loading, setLoading] = useState(true); // Set loading to true on component mount
  const [users, setUsers] = useState([]); // Initial empty array of users

  useEffect(() => {
    const subscriber = db
    .collection('food')
    .onSnapshot(querySnapshot => {
      const users = [];

      querySnapshot.forEach(documentSnapshot => {
        users.push({
          ...documentSnapshot.data(),
          key: documentSnapshot.id,
        });
      });

      setUsers(users);
      setLoading(false);
    });

  // Unsubscribe from events when no longer in use
  return () => subscriber();
  }, []);

  if (loading) {
    return <ActivityIndicator />;
  }
  
  const Separator = () => (
    <View style={styless.separator} />
  );
  
  const Item = ({ title,img }) => (
    <View style={styles.item}>
      <Image
       style={{width: '100%', height: 200,resizeMode : 'stretch' }}
       source={{uri: title}} 
       />  
    </View>
  );

  const renderItem = ({ item }) => (
    <Item title={item.img} />
  );


  return (
    <SafeAreaView style={styles.container}>
    <FlatList
      data={users}
      renderItem={renderItem}
      keyExtractor={item => item.id}
//        <View style={{ height: 50, flex: 1, alignItems: 'center', justifyContent: 'center' }}>
  //        <Text style={styles.title}>User Name: {item.name}</Text>
    //      <Text style={styles.title}>User id: {item.id}</Text>
     //     <Image
   //style={{width: '100%', height: 200,resizeMode : 'stretch' }}
   //source={{uri: item.img}} 
 ///>  
   //     </View>

     // )}
    />
    </SafeAreaView>
  );
}




export default Users;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    backgroundColor: '#f9c2ff',
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 16,
  },
  title: {
    fontSize: 32,
  },
});
